#include "GuiControl.h"

GuiControl::GuiControl(QObject *parent) :
    QObject(parent)
{
    //Connecting local slot to signal generated by Standard view
    connect(&standard,SIGNAL(relayInputStan(QString)),
            this,SLOT(runInput(QString)));

    //Connecting local slot to signal generated by Standard view
    connect(&seample,SIGNAL(relayInputSeam(QString)),
            this,SLOT(runInput(QString)));
}

void GuiControl:: showGui()
{
    if (standardViewFlag)
    {
        standard.show();
    }
    else
    {
        seample.show();
    }
}

void GuiControl::setStandardView (bool flag)
{
    standardViewFlag = flag;
}

bool GuiControl::isStandardView()
{
    return standardViewFlag;
}

void GuiControl::runInput(QString input)
{
    qDebug() << input;
}
